options.helpers:
// Selecionar o elemento onde o status será inserido
let versionStatus = document.getElementById('version-status');

// Verificar se o elemento foi encontrado no DOM antes de manipulá-lo
if (versionStatus) {
  // Atualizar o texto e a classe com base no conteúdo atual
  let status = versionStatus.textContent.trim();

  if (status === '1') {
    versionStatus.textContent = 'Online';
    versionStatus.classList.add('online');
  } else if (status === '0') {
    versionStatus.textContent = 'Offline';
    versionStatus.classList.add('offline');
  } else {
    console.error('Conteúdo inválido para atualização de status.');
  }
} else {
  console.error('Elemento #version-status não encontrado no DOM.');
}

